class MyQueue(object):
    def __init__(self):

        self.head = ListNode(0)
        self.tail = self.head

    def enqueue(self, item):

        node = ListNode(item)
        self.tail.next = node
        self.tail = node
    def dequeue(self):

        if self.head.next is None:
            return None

        val = self.head.next.val
        self.head = self.head.next
        return val

